<script type="text/javascript">
    $(document).ready(function () {

        var targetFlag = 0;
        setInterval(function () {
            slideBanner("left", "auto", targetFlag);
            targetFlag++;
            //console.log(targetFlag);
        }, 2000);

        //dir = 往哪個方向滑,type 區分是自動輪播還是人為滑動,targetIndex為要滑到第幾個
        function slideBanner(dir, type, targetIndex) {
            var target = "";

            targetIndex = targetIndex % 3;
            target = targetIndex.toString();
            switch (target) {
                case "0":
                    moveto(0, "-785px", "animate");
                    moveto(1, "0px", "animate");
                    moveto(2, "785px", "static");
                    break;
                case "1":
                    moveto(1, "-785px", "animate");
                    moveto(2, "0px", "animate");
                    moveto(0, "785px", "static");
                    break;
                case "2":
                    moveto(2, "-785px", "animate");
                    moveto(0, "0px", "animate");
                    moveto(1, "785px", "static");
                    break;

            }

        }

        function moveto(target, pos, type) {
            if (type == "animate") {
                $("#Stage_pic_slide_mask_slide_" + target).animate({
                    left: pos
                }, 500, function () {
                    swipeMode = false;
                });
            } else {
                $("#Stage_pic_slide_mask_slide_" + target).css("left", pos, function () {
                    swipeMode = false;
                });
            }
        }

        var targetindex = 0;
        var fixed = false;
        $(".noChgHeight #pmenu_box").hide();
        $(window).scroll(function () {
            $.each($("section"), function (i, v) {
                if (i > 0) {
                    if ($.isScrolledIntoViewOverWindow($(this), 0)) {
                        console.log("t:" + targetindex + ",i:" + i);
                        if (targetindex != i) {
                            $(".highLineActive").removeClass("highLineActive");
                            switch (i) {
                                case 2:
                                    $("#section_3 .mobileTitle").html("WHAT");
                                    $(".highLineTitle:eq(0)").addClass("highLineActive");
                                    break;
                                case 3:
                                    $("#section_3 .mobileTitle").html("WHY");
                                    $(".highLineTitle:eq(1)").addClass("highLineActive");
                                    break;
                                case 4:
                                    $("#section_3 .mobileTitle").html("HOW");
                                    $(".highLineTitle:eq(2)").addClass("highLineActive");
                                    break;
                            }
                            targetindex = i;
                        }
                        return false;
                    }
                }
            });

            var headHeight = $("#section_1").height() + $("#section_2").height() - 200;//console.log(headHeight);console.log($(this).scrollTop() );
            if ($(this).scrollTop() > headHeight) {
                if (!fixed) {
                    fixed = true;
                    $(".noChgHeight #pmenu_box").show().removeClass("mouseZoomOut").addClass("fixedTop").addClass("mouseZoomIn").css({"top": "69px"});
                }
            } else {
                fixed = false;
                $(".noChgHeight #pmenu_box").hide().removeClass("mouseZoomIn").removeClass("fixedTop").addClass("mouseZoomOut");

            }
        });

        $(".goto").click(function () {
            var targetIndex = $(this).attr("data-target");
            $('html, body').animate({
                scrollTop: $("section").eq(targetIndex).offset().top - 66
            }, 1000);
        });

        var top = 30;
        var right = 0;
        $(".animationImg").each(function () {
            $(this).attr("data-Animation", "fadeInRight");
            $(this).css("top", top + "px");
            $(this).css("left", right + "px");
            //$(this).css("height", "100%");
            //$(this).css("width","100%");
            //top += 0;
            right += 90;
        });
    });
</script>